    //å®šä¹‰å˜é‡
    private String name;
    private int age;

    //æ„é€ æ–¹æ³•
    public Student() {
    }

    public Student(String name, int age) {
        this.name = name;
        this.age = age;
    }

    //æˆå‘˜æ–¹æ³•
    public void setName(String name) {
        this.name = name;
    }

